import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Badge, Dropdown, Modal, Alert } from 'react-bootstrap';
import './App.css';
import ReactDOM from 'react-dom';
import { calculateTotalWithFee } from './utils';

