    setTotalTicketPrice(newTotalTicketPrice);

    clearTimeout(purchaseTimeout);
    const timeoutId = setTimeout(() => {
      handleCloseModal();
      alert('Purchase session expired. Please try again.');
    }, 5 * 60 * 1000);
    setPurchaseTimeout(timeoutId);
