    //setTotalTicketPrice(totalWithFee);


    // Calculate the fee
//    const feeAmount = (newTotalTicketPrice * feePercentage) / 100;

    // Calculate the total ticket price including the fee
//    const totalWithFee = newTotalTicketPrice + feeAmount;
    //console.log('feeAmount:', feeAmount);
    //console.log('Reservation total:', totalWithFee);

    } else if (selectedSpots > 8) {
      alert("You can't reserve more than 8 spots for a single table.");
    } else {
