// utils.js

export const calculateTotalWithFee = (ticketTypes, ticketPrices, trasfeePercentage, feePercentage) => {
  const totalTicketPrice = ticketTypes.reduce((total, type) => total + ticketPrices[type], 0);
  const trnsfee = (totalTicketPrice * trasfeePercentage) / 100 + 0.30;
  const feeAmount = (totalTicketPrice * feePercentage) / 100;
  return {
    totalWithFee: totalTicketPrice + trnsfee + feeAmount,
	trnsfee,
	feeAmount,
  };
};
